mapper.select:
select cg_code,cg_parent,cg_name
from category
where cg_parent = #{cg_parent}

select prd_no,rev_count,cg_code,cg_parent,prd_name,prd_price,prd_discount,prd_company,prd_detail,prd_img,prd_stock,prd_date_post,prd_date_up
from (select prd_no,rev_count,cg_code,cg_parent,prd_name,prd_price,prd_discount,prd_company,prd_detail,prd_img,prd_stock,prd_date_post,prd_date_up,
	  row_number() over(order by prd_no desc) prd_seq
	  from product
	  where cg_code=#{cg_code})  
where prd_seq between #{rowStart} and #{rowEnd}

select cg_name 
from category
where cg_code=#{cg_code}

select count(prd_no)
from product
where cg_code=#{cg_code}

	select prd_no,rev_count,cg_code,cg_parent,prd_name,prd_price,prd_discount,prd_company,prd_detail,prd_img,prd_stock,prd_date_post,prd_date_up
	from (select prd_no,rev_count,cg_code,cg_parent,prd_name,prd_price,prd_discount,prd_company,prd_detail,prd_img,prd_stock,prd_date_post,prd_date_up,
		  row_number()over(order by prd_no desc) prd_seq
    	  from product)
	where prd_seq between #{rowStart} and #{rowEnd}
