mapper.select:
	select prd_no,prd_name,prd_cost,prd_price,prd_discount,prd_stock,prd_total_sales,prd_company 
	from (select prd_no,prd_name,prd_cost,prd_price,prd_discount,prd_stock,prd_total_sales,prd_company,
			row_number() over(order by prd_no desc) stock_seq
			from product
)
	where stock_seq between #{rowStart} and #{rowEnd}
